mapper.select:
select id, user_name as 'userName', real_name as 'realName', level as 'level', dept_id as 'deptId'
from p_user
where user_name = #{userName} and user_pwd = #{userPwd};

select id, user_name as 'userName', real_name as 'realName', level as 'level', dept_id as 'deptId'
from p_user
where id=#{id}
